{
  "loop_id": "loop_kimi_001",
  "source_file": "conversations_normalized.json",
  "timestamp": "2025-09-17T14:00:00",
  "title": "Kimi Clarifies the IRS Line",
  "presence_score": null,
  "metrics": {
