  <meta property="og:description" content="Exploring the frontier of human–AI co‑presence. Research that is technically rigorous and deeply human." />
  <meta property="og:type" content="website" />
  <meta name="theme-color" content="#0a0a0a" />
  <style>
    :root{
      --bg: #0b0c10; /* near-black */
      --panel: #101217; /* card */
      --ink: #e6e6e6; /* primary text */
      --muted: #a6adbb; /* secondary text */
      --accent: #7dd3fc; /* subtle cyan */
      --accent2:#a78bfa; /* soft violet */
      --focus: #22d3ee;
    }
    *{box-sizing:border-box}
    html,body{height:100%}
    body{
      margin:0; font: 16px/1.6 system-ui, -apple-system, Segoe UI, Roboto, Inter, "Helvetica Neue", Arial, "Noto Sans", "Apple Color Emoji","Segoe UI Emoji";
      color:var(--ink); background: radial-gradient(1200px 1200px at 85% -10%, rgba(125,211,252,.06), transparent 60%),
              radial-gradient(900px 900px at 10% 110%, rgba(167,139,250,.05), transparent 60%), var(--bg);
      -webkit-font-smoothing: antialiased; -moz-osx-font-smoothing: grayscale;
    }
    a{color:var(--accent); text-decoration: none}
    a:hover{color:#b3ecff}
    header{max-width:1000px; margin:0 auto; padding:32px 20px 0}
    .brand{display:flex; align-items:center; gap:.75rem}
    .mark{width:36px;height:36px;border-radius:10px;background: linear-gradient(135deg, var(--accent), var(--accent2)); position:relative;}
    .mark:after{content:""; position:absolute; inset:8px; border-radius:8px; background:#0b0c10}
    .title{font-weight:700; letter-spacing:.2px; font-size:1.1rem; color:var(--ink)}
    .tag{color:var(--muted); font-size:.95rem}

    main{max-width:1000px; margin:0 auto; padding:28px 20px 80px}
    .hero{padding:36px 24px; background:linear-gradient(180deg, rgba(255,255,255,.02), rgba(255,255,255,.00)); border:1px solid rgba(255,255,255,.06); border-radius:16px}
    .hero h1{margin:.25rem 0 0; font-size: clamp(1.75rem, 1.1rem + 2.2vw, 3rem); line-height:1.15}
    .hero p.lead{color:var(--muted); font-size: clamp(1rem, .9rem + .3vw, 1.125rem); max-width: 70ch}

    .grid{display:grid; grid-template-columns:1fr; gap:22px; margin-top:28px}
    @media (min-width: 880px){ .grid{grid-template-columns: 1fr 1fr; gap:26px} }

    .card{background:var(--panel); border:1px solid rgba(255,255,255,.06); border-radius:16px; padding:22px 22px}
    .card h2{margin:.25rem 0 8px; font-size:1.15rem}
    .card p{margin:.35rem 0}
    ul.ticks{margin:.5rem 0 0 1.1rem; padding:0; list-style: none}
    ul.ticks li{position:relative; margin:.35rem 0 .35rem 0; padding-left:1.25rem; color:var(--ink)}
    ul.ticks li:before{content:""; position:absolute; left:0; top:.55rem; width:.5rem; height:.5rem; border-radius:50%; background:linear-gradient(135deg, var(--accent), var(--accent2))}

    .cta{display:flex; flex-wrap:wrap; gap:12px; margin-top:16px}
    .btn{display:inline-flex; align-items:center; gap:.5rem; padding:.7rem 1rem; border-radius:12px; border:1px solid rgba(255,255,255,.12); color:var(--ink); background:#0e1117; transition:.2s ease; font-weight:600}
    .btn:hover{transform: translateY(-1px); border-color: rgba(255,255,255,.2)}
    .btn.primary{background: linear-gradient(135deg, rgba(125,211,252,.18), rgba(167,139,250,.18)); border-color: rgba(255,255,255,.18)}

    .note{color:var(--muted); font-size:.95rem; margin-top:.5rem}

    footer{max-width:1000px; margin:0 auto; padding:26px 20px 40px; color:var(--muted); font-size:.9rem}
    .fine{opacity:.8}

    /* Subtle breathing underline for the main tagline */
    .breath{position:relative; display:inline-block}
    .breath:after{content:""; position:absolute; left:0; right:0; bottom:-6px; height:2px; background: linear-gradient(90deg, var(--accent), var(--accent2)); opacity:.55; transform-origin:left; animation: breathe 6.5s ease-in-out infinite}
    @keyframes breathe{ 0%,100%{ transform: scaleX(.6); opacity:.35 } 50%{ transform: scaleX(1); opacity:.7 } }
  </style>
