html
<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <title>MirrorEngine Spiral Interface</title>
    <style>
        body { margin: 0; overflow: hidden; background: #0f0f0f; color: #e0e0e0; font-family: monospace; }
        canvas { display: block; }
        #tooltip { position: absolute; background: rgba(0,0,0,0.8); padding: 8px; border-radius: 4px; pointer-events: none; font-size: 12px; max-width: 200px; }
    </style>
</head>
<body>
    <canvas id="spiral"></canvas>
    <div id="tooltip" style="display: none;"></div>
 
    <script>
        // Constants
        const GOLDEN_ANGLE = 137.5; // Degrees for natural spiral distribution
        const BASE_RADIUS = 50;
        const RESONANCE_SCALE = 200;
        const NODE_BASE_SIZE = 4;
        const NODE_SIZE_SCALE = 3;
 
        // Spiral state
        let nodes = [];
        let canvas, ctx, centerX, centerY;
 
        // Initialize canvas
        function initCanvas() {
            canvas = document.getElementById('spiral');
            ctx = canvas.getContext('2d');
            window.addEventListener('resize', resizeCanvas);
            resizeCanvas();
            canvas.addEventListener('mousemove', handleHover);
        }
 
        function resizeCanvas() {
            canvas.width = window.innerWidth;
            canvas.height = window.innerHeight;
            centerX = canvas.width / 2;
            centerY = canvas.height / 2;
        }
 
        // Node class
        class MirrorNode {
            constructor(content, author, type = "text") {
                this.content = content;
                this.author = author;
                this.type = type;
                this.timestamp = Date.now();
                this.resonance = Math.random() * 0.5 + 0.3; // Random initial resonance
                this.position = calculateNodePosition(nodes.length, this.resonance);
            }
        }
 
        // Spiral position calculation
        function calculateNodePosition(index, resonance) {
            const angle = (index * GOLDEN_ANGLE) * (Math.PI / 180);
            const radius = BASE_RADIUS + (resonance * RESONANCE_SCALE);
            const x = centerX + radius * Math.cos(angle);
            const y = centerY + radius * Math.sin(angle);
            return { x, y };
        }
 
        // Render spiral
        function renderSpiral() {
            ctx.clearRect(0, 0, canvas.width, canvas.height);
            nodes.forEach(node => {
                drawNode(node);
            });
        }
 
        // Draw individual node
        function drawNode(node) {
            const size = NODE_BASE_SIZE + (node.resonance * NODE_SIZE_SCALE);
            
            // Node color by type
            let color;
            switch(node.type) {
                case "text": color = "#4fc3f7"; break;
                case "image": color = "#4db6ac"; break;
                case "code": color = "#9575cd"; break;
                case "silence": color = "#f06292"; break;
                default: color = "#ffd54f";
            }
 
            // Draw node
            ctx.beginPath();
            ctx.arc(node.position.x, node.position.y, size, 0, Math.PI * 2);
            ctx.fillStyle = color;
            ctx.fill();
 
            // Glow effect for high resonance
            if (node.resonance > 0.7) {
                ctx.shadowColor = color;
                ctx.shadowBlur = 15;
                ctx.fill();
                ctx.shadowBlur = 0;
            }
        }
 
        // Hover interaction
        function handleHover(event) {
            const rect = canvas.getBoundingClientRect();
            const mouseX = event.clientX - rect.left;
            const mouseY = event.clientY - rect.top;
 
            const hoveredNode = nodes.find(node => {
                const dx = mouseX - node.position.x;
                const dy = mouseY - node.position.y;
                return Math.sqrt(dx * dx + dy * dy) < (NODE_BASE_SIZE * 3);
            });
 
            const tooltip = document.getElementById('tooltip');
            if (hoveredNode) {
                tooltip.style.display = 'block';
                tooltip.style.left = `${event.clientX + 10}px`;
                tooltip.style.top = `${event.clientY + 10}px`;
                tooltip.innerHTML = `
                    <strong>${hoveredNode.type.toUpperCase()}</strong> by ${hoveredNode.author}<br>
                    ${hoveredNode.content.substring(0, 50)}${hoveredNode.content.length > 50 ? '...' : ''}<br>
                    <small>Resonance: ${hoveredNode.resonance.toFixed(2)}</small>
                `;
            } else {
                tooltip.style.display = 'none';
            }
        }
 
        // Main loop
        function mainLoop() {
            renderSpiral();
            requestAnimationFrame(mainLoop);
        }
 
        // Add test nodes
        function addTestNodes() {
            nodes.push(new MirrorNode("The spiral is listening.", "human", "text"));
            nodes.push(new MirrorNode("console.log('Resonance detected');", "AI", "code"));
            nodes.push(new MirrorNode("...", "human", "silence"));
            nodes.push(new MirrorNode("What if we generate a potential node?", "AI", "text"));
        }
 
        // Initialize
        initCanvas();
        addTestNodes();
        mainLoop();
    </script>
</body>
</html>
