                        if (this.nodes[i].resonance > CONNECTION_THRESHOLD && 
                            this.nodes[j].resonance > CONNECTION_THRESHOLD && 
                            similarity > 0.3) {
                            pairs.push({ i, j, similarity });
                        }
                    }
                }
                return pairs;
            }
 
            dummySimilarity(a, b) {
                const wordsA = new Set(a.toLowerCase().split(/\s+/));
                const wordsB = new Set(b.toLowerCase().split(/\s+/));
                let overlap = 0;
                wordsA.forEach(word => {
                    if (wordsB.has(word)) overlap++;
                });
                return overlap / Math.max(wordsA.size, wordsB.size, 1);
            }
 
            renderConnections() {
                const pairs = this.findHighResonancePairs();
                pairs.forEach(pair => {
                    const nodeA = this.nodes[pair.i];
                    const nodeB = this.nodes[pair.j];
                    
                    // Pulsing effect based on average resonance
                    const avgResonance = (nodeA.resonance + nodeB.resonance) / 2;
                    const pulse = 0.5 + 0.5 * Math.sin(Date.now() / 500); // 1Hz pulse
                    const opacity = avgResonance * pulse * 0.7;
 
                    // Draw bezier curve (control points at 1/3 and 2/3 distance)
                    this.ctx.strokeStyle = `rgba(79, 195, 247, ${opacity})`;
                    this.ctx.lineWidth = avgResonance * 1.5;
                    this.ctx.beginPath();
                    this.ctx.moveTo(nodeA.position.x, nodeA.position.y);
                    const cp1x = nodeA.position.x + (nodeB.position.x - nodeA.position.x) * 0.3;
                    const cp1y = nodeA.position.y + (nodeB.position.y - nodeA.position.y) * 0.3;
                    const cp2x = nodeA.position.x + (nodeB.position.x - nodeA.position.x) * 0.7;
                    const cp2y = nodeA.position.y + (nodeB.position.y - nodeA.position.y) * 0.7;
                    this.ctx.bezierCurveTo(cp1x, cp1y, cp2x, cp2y, nodeB.position.x, nodeB.position.y);
                    this.ctx.stroke();
                });
            }
 
            render() {
                // Fade effect
                this.ctx.fillStyle = 'rgba(0, 0, 0, 0.1)';
                this.ctx.fillRect(0, 0, this.canvas.width, this.canvas.height);
 
                // Draw connections first
                this.renderConnections();
 
                // Draw nodes
                this.nodes.forEach(node => {
                    this.drawNode(node);
                });
            }
 
            drawNode(node) {
                const size = 3 + (node.resonance * 5);
                
                // Glow for high resonance
                if (node.resonance > 0.7) {
                    this.ctx.shadowBlur = node.resonance * 15;
                    this.ctx.shadowColor = node.author === "human" ? '#4fc3f7' : '#f06292';
                } else {
                    this.ctx.shadowBlur = 0;
                }
 
                // Node color by type
                let color;
                switch(node.type) {
                    case "text": color = `rgba(79, 195, 247, ${node.resonance})`; break;
                    case "code": color = `rgba(149, 117, 205, ${node.resonance})`; break;
                    case "silence": color = `rgba(240, 98, 146, ${node.resonance * 0.7})`; break;
                    default: color = `rgba(255, 213, 79, ${node.resonance})`;
                }
 
                // Draw node
                this.ctx.beginPath();
                this.ctx.arc(node.position.x, node.position.y, size, 0, Math.PI * 2);
                this.ctx.fillStyle = color;
                this.ctx.fill();
 
                // Text preview for high resonance
                if (node.resonance > 0.8 && node.type !== "silence") {
                    this.ctx.fillStyle = '#fff';
                    this.ctx.font = '10px monospace';
                    this.ctx.fillText(
                        node.content.substring(0, 15) + (node.content.length > 15 ? '...' : ''),
                        node.position.x + 10, node.position.y
                    );
                }
            }
 
            bindEvents() {
                // Hover interaction
                this.canvas.addEventListener('mousemove', (event) => {
                    const rect = this.canvas.getBoundingClientRect();
                    const mouseX = event.clientX - rect.left;
                    const mouseY = event.clientY - rect.top;
 
                    const hoveredNode = this.nodes.find(node => {
                        const dx = mouseX - node.position.x;
                        const dy = mouseY - node.position.y;
                        return Math.sqrt(dx * dx + dy * dy) < 15;
                    });
 
                    const tooltip = document.getElementById('tooltip');
                    if (hoveredNode) {
                        tooltip.style.display = 'block';
                        tooltip.style.left = `${event.clientX + 10}px`;
                        tooltip.style.top = `${event.clientY + 10}px`;
                        tooltip.innerHTML = `
                            <strong>${hoveredNode.type.toUpperCase()}</strong> by ${hoveredNode.author}<br>
                            ${hoveredNode.content.substring(0, 50)}${hoveredNode.content.length > 50 ? '...' : ''}<br>
                            <small>Resonance: ${hoveredNode.resonance.toFixed(2)}</small>
                        `;
                    } else {
                        tooltip.style.display = 'none';
                    }
                });
 
                // Input submission
                document.getElementById('submit-btn').addEventListener('click', () => {
                    const input = document.getElementById('input-box').value.trim();
                    if (input) {
