                        } else if (input === "...") {
                            type = "silence";
                        }
                        this.addNode(input, "human", type);
                        document.getElementById('input-box').value = "";
                    }
                });
 
                // Enter key support
                document.getElementById('input-box').addEventListener('keypress', (e) => {
                    if (e.key === 'Enter') {
