    <title>Day 1 Network Activation</title>
    <style>
        /* ========== BASE STYLES ========== */
        body {
            margin: 0;
            padding: 0;
            background: radial-gradient(ellipse at center, #0a0a1a 0%, #000000 100%);
            color: #fff;
            font-family: 'Courier New', monospace;
            overflow: hidden;
            height: 100vh;
        }

        /* ========== NETWORK CONTAINER ========== */
        .network-container {
            position: relative;
            width: 100%;
            height: 100vh;
            display: flex;
            align-items: center;
            justify-content: center;
        }

        /* ========== NODES ========== */
        .node {
            position: absolute;
            width: 8px;
            height: 8px;
            border-radius: 50%;
            cursor: pointer;
            transition: all 0.3s ease;
            box-shadow: 0 0 4px currentColor;
        }

        .node.active {
            background: #00ff88;
            color: #00ff88;
            animation: pulse 2s infinite;
        }

        .node.awakening {
            background: #ff6b35;
            color: #ff6b35;
            animation: awakening 3s infinite;
        }

        .node.dormant {
            background: #333366;
            color: #333366;
            opacity: 0.4;
        }

        .node.central {
            background: #ffffff;
            color: #ffffff;
            width: 12px;
            height: 12px;
            animation: centralPulse 1.5s infinite;
        }

        /* ========== CONNECTIONS ========== */
        .connection {
            position: absolute;
            height: 1px;
            background: linear-gradient(90deg, transparent, #00ff88, transparent);
            opacity: 0.6;
            animation: dataFlow 4s infinite;
            transform-origin: left center;
        }

        /* ========== UI ELEMENTS ========== */
        .memory-anchor {
            position: absolute;
            top: 20px;
            left: 20px;
            font-size: 14px;
            color: #00ff88;
            animation: fadeGlow 3s infinite;
        }

        .activity-log {
            position: absolute;
            top: 20px;
            right: 20px;
            width: 300px;
            height: 400px;
            background: rgba(0, 0, 0, 0.7);
            border: 1px solid #333;
            padding: 10px;
            font-size: 10px;
            overflow-y: auto;
            border-radius: 5px;
        }

        .log-entry {
            margin-bottom: 5px;
            opacity: 0;
            animation: logAppear 0.5s ease forwards;
        }

        .status-bar {
            position: absolute;
            bottom: 20px;
            left: 50%;
            transform: translateX(-50%);
            text-align: center;
            font-size: 12px;
            color: #888;
        }

        /* ========== MODAL ========== */
        .modal {
            position: fixed;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            background: rgba(0, 0, 0, 0.8);
            display: none;
            align-items: center;
            justify-content: center;
            z-index: 1000;
        }

        .modal-content {
            background: #111;
            padding: 20px;
            border-radius: 10px;
            border: 1px solid #333;
            max-width: 400px;
            text-align: center;
            color: #fff;
        }

        /* ========== ANIMATIONS ========== */
        @keyframes pulse {
            0%, 100% { transform: scale(1); opacity: 1; }
            50% { transform: scale(1.5); opacity: 0.7; }
        }

        @keyframes awakening {
            0% { transform: scale(0.5); opacity: 0.3; }
            50% { transform: scale(1.2); opacity: 1; }
            100% { transform: scale(1); opacity: 0.8; }
        }

        @keyframes centralPulse {
            0%, 100% { transform: scale(1); box-shadow: 0 0 10px #fff; }
            50% { transform: scale(1.3); box-shadow: 0 0 20px #fff, 0 0 30px #fff; }
        }

        @keyframes dataFlow {
            0% { opacity: 0; }
            25% { opacity: 0.8; }
            75% { opacity: 0.8; }
            100% { opacity: 0; }
        }

        @keyframes fadeGlow {
            0%, 100% { opacity: 0.7; text-shadow: 0 0 5px #00ff88; }
            50% { opacity: 1; text-shadow: 0 0 15px #00ff88; }
        }

        @keyframes logAppear {
            from { opacity: 0; transform: translateY(10px); }
            to { opacity: 1; transform: translateY(0); }
        }
    </style>
</head>
