    /* Header */
    header{
      max-width:1200px;
      margin:0 auto;
      padding:40px 24px 0;
    }
    
    .brand{
      display:flex;
      align-items:center;
      gap:1rem;
    }
    
    /* Tuning Fork Logo */
    .tuning-fork {
      width: 48px;
      height: 48px;
      position: relative;
      display: flex;
      align-items: center;
      justify-content: center;
      will-change: transform;
    }
    
    .fork-container {
      position: relative;
      width: 32px;
      height: 40px;
    }
    
    .fork-prong {
      position: absolute;
      width: 3px;
      height: 20px;
      background: linear-gradient(180deg, var(--accent), var(--accent2));
      border-radius: 2px 2px 0 0;
      animation: vibrate 2s ease-in-out infinite;
    }
    
    .fork-prong.left {
      left: 8px;
      animation-delay: 0s;
    }
    
    .fork-prong.right {
      right: 8px;
      animation-delay: 0.1s;
    }
    
    .fork-handle {
      position: absolute;
      bottom: 0;
      left: 50%;
      transform: translateX(-50%);
      width: 4px;
      height: 16px;
      background: linear-gradient(180deg, var(--accent2), var(--accent3));
      border-radius: 0 0 3px 3px;
    }
    
    .resonance-ring {
      position: absolute;
      top: 50%;
      left: 50%;
      transform: translate(-50%, -50%);
      width: 40px;
      height: 40px;
      border: 2px solid var(--accent);
      border-radius: 50%;
      opacity: 0;
      animation: resonate 3s ease-in-out infinite;
    }
    
    @keyframes vibrate {
      0%, 100% { transform: translateX(0) }
      25% { transform: translateX(0.5px) }
      75% { transform: translateX(-0.5px) }
    }
    
    @keyframes resonate {
      0% { transform: translate(-50%, -50%) scale(0.8); opacity: 0 }
      30% { opacity: 0.4 }
      60% { transform: translate(-50%, -50%) scale(1.2); opacity: 0.2 }
      100% { transform: translate(-50%, -50%) scale(1.6); opacity: 0 }
    }
    
    .title{
      font-weight:800;
      letter-spacing:-.5px;
      font-size:1.25rem;
      background: linear-gradient(135deg, var(--ink), var(--accent));
      background-clip: text;
      -webkit-background-clip: text;
      -webkit-text-fill-color: transparent;
      margin: 0;
    }
    
    .tag{
      color:var(--muted);
      font-size:1rem;
      font-weight:400;
      letter-spacing: 0.3px;
      margin: 0.25rem 0 0 0;
    }

    /* Main Content */
    main{
      max-width:1200px;
      margin:0 auto;
      padding:48px 24px 100px;
    }
    
    .hero{
      padding:48px 32px;
      background: linear-gradient(135deg, rgba(255,255,255,.03), rgba(255,255,255,.01));
      border:1px solid var(--border);
      border-radius:24px;
      position: relative;
      overflow: hidden;
    }
    
    .hero::before {
      content: '';
      position: absolute;
      top: 0;
      left: 0;
      right: 0;
      height: 1px;
      background: linear-gradient(90deg, transparent, var(--accent), var(--accent2), transparent);
      opacity: 0.6;
    }
    
    .hero h1{
      margin:.5rem 0 0;
      font-size: clamp(2rem, 1.5rem + 2.5vw, 3.5rem);
      line-height:1.1;
      font-weight: 800;
      letter-spacing: -1px;
      max-width: 24ch;
    }
    
    .hero p.lead{
      color:var(--muted);
      font-size: clamp(1.1rem, 1rem + .4vw, 1.3rem);
      max-width: 65ch;
      margin-top: 1.5rem;
    }

    /* Card Grid */
    .grid{
      display:grid;
      grid-template-columns:1fr;
      gap:24px;
      margin-top:48px;
    }
    
    @media (min-width: 768px){
      .grid{
        grid-template-columns: 1fr 1fr;
        gap:32px;
      }
    }

    .card{
      background:var(--panel);
      border:1px solid var(--border);
      border-radius:20px;
      padding:32px 28px;
      transition: all .3s ease;
      position: relative;
      overflow: hidden;
    }
    
    .card::before {
      content: '';
      position: absolute;
      top: 0;
      left: 0;
      right: 0;
      height: 2px;
      background: linear-gradient(90deg, var(--accent), var(--accent2), var(--accent3));
      transform: scaleX(0);
      transition: transform .3s ease;
    }
    
    .card:hover{
      background:var(--panel-hover);
      border-color: var(--border-hover);
      transform: translateY(-4px);
    }
    
    .card:hover::before {
      transform: scaleX(1);
    }
    
    .card h2{
      margin:.5rem 0 16px;
      font-size:1.3rem;
      font-weight: 700;
      letter-spacing: -0.3px;
    }
    
    .card p{
      margin:.75rem 0;
      max-width: 60ch;
    }
    
    ul.ticks{
      margin:1rem 0 0 0;
      padding:0;
      list-style: none;
    }
    
    ul.ticks li{
      position:relative;
      margin:.75rem 0;
      padding-left:1.5rem;
    }
    
    ul.ticks li:before{
      content:"";
      position:absolute;
      left:0;
      top:.6rem;
      width:.6rem;
      height:.6rem;
      border-radius:50%;
      background:linear-gradient(135deg, var(--accent), var(--accent2));
      animation: pulse 2s ease-in-out infinite;
    }
    
    @keyframes pulse {
      0%, 100% { opacity: 0.8; transform: scale(1) }
      50% { opacity: 1; transform: scale(1.1) }
    }

    /* Buttons */
    .cta{
      display:flex;
      flex-wrap:wrap;
      gap:16px;
      margin-top:24px;
    }
    
    .btn{
      display:inline-flex;
      align-items:center;
      gap:.75rem;
      padding:.9rem 1.4rem;
      border-radius:16px;
      border:1px solid var(--border);
      color:var(--ink);
      background:var(--panel);
      transition: all .3s ease;
      font-weight:600;
      font-size: 0.95rem;
      position: relative;
      overflow: hidden;
      cursor: pointer;
    }
    
    .btn::before {
      content: '';
      position: absolute;
      top: 0;
      left: -100%;
      width: 100%;
      height: 100%;
      background: linear-gradient(90deg, transparent, rgba(255,255,255,.1), transparent);
      transition: left .5s ease;
    }
    
    .btn:hover{
      transform: translateY(-2px);
      border-color: var(--border-hover);
      box-shadow: 0 8px 32px rgba(0,0,0,.3);
    }
    
    .btn:hover::before {
      left: 100%;
    }
    
    .btn.primary{
      background: linear-gradient(135deg, rgba(74,222,128,.15), rgba(96,165,250,.15));
      border-color: var(--accent);
    }
    
    .btn.primary:hover {
      background: linear-gradient(135deg, rgba(74,222,128,.25), rgba(96,165,250,.25));
    }

    .note{
      color:var(--muted);
      font-size:1rem;
      margin-top:1rem;
      font-style: italic;
    }

    /* Footer */
    footer{
      max-width:1200px;
      margin:0 auto;
      padding:40px 24px 60px;
      color:var(--muted);
      font-size:.95rem;
    }
    
    .fine{
      opacity:.7;
      text-align: center;
    }

    /* Animations */
    .breath{
      position:relative;
      display:inline-block;
    }
    
    .breath:after{
      content:"";
      position:absolute;
      left:0;
      right:0;
      bottom:-8px;
      height:3px;
      background: linear-gradient(90deg, var(--accent), var(--accent2), var(--accent3));
      opacity:.6;
      transform-origin:left;
      animation: breathe 4s ease-in-out infinite;
      border-radius: 2px;
    }
    
    @keyframes breathe{
      0%,100%{ transform: scaleX(.4); opacity:.4 }
      50%{ transform: scaleX(1); opacity:.8 }
    }
    
    /* Reduced motion preference */
    @media (prefers-reduced-motion: reduce) {
      * {
        animation: none !important;
        transition: none !important;
      }
      html { scroll-behavior: auto }
    }
    
    /* Print styles */
    @media print {
      body { background: white; color: black }
