    }
    
    .card:hover{
      background:var(--panel-hover); 
      border-color: var(--border-hover);
      transform: translateY(-4px);
    }
    
    .card:hover::before {
      transform: scaleX(1);
    }
    
    .card h2{
      margin:.5rem 0 16px; 
      font-size:1.3rem;
      font-weight: 700;
      letter-spacing: -0.3px;
    }
    
    .card p{
      margin:.75rem 0;
      line-height: 1.7;
    }
    
    ul.ticks{
      margin:1rem 0 0 0; 
      padding:0; 
      list-style: none;
    }
    
    ul.ticks li{
      position:relative; 
      margin:.75rem 0; 
      padding-left:1.5rem; 
      color:var(--ink);
      line-height: 1.6;
    }
    
    ul.ticks li:before{
      content:""; 
      position:absolute; 
      left:0; 
      top:.6rem; 
      width:.6rem; 
      height:.6rem; 
      border-radius:50%; 
      background:linear-gradient(135deg, var(--accent), var(--accent2));
      animation: pulse 2s ease-in-out infinite;
    }
    
    @keyframes pulse {
      0%, 100% { opacity: 0.8; transform: scale(1) }
