    }

    .cta{
      display:flex; 
      flex-wrap:wrap; 
      gap:16px; 
      margin-top:24px;
    }
    
    .btn{
      display:inline-flex; 
      align-items:center; 
      gap:.75rem; 
      padding:.9rem 1.4rem; 
      border-radius:16px; 
      border:1px solid var(--border); 
      color:var(--ink); 
