from pptx.dml.color import RGBColor
from pptx.util import Pt

# Define the color scheme based on the Lumina Solar logo
primary_color = RGBColor(0, 112, 192)  # Blue from the logo
secondary_color = RGBColor(255, 255, 255)  # White from the logo
accent_color = RGBColor(200, 200, 200)  # Light Gray

# Function to set the slide background color
def set_slide_background(slide, color):
    background = slide.background
    fill = background.fill
    fill.solid()
    fill.fore_color.rgb = color

# Function to set the text color of all text in a slide
def set_text_color(slide, color):
    for shape in slide.shapes:
        if not shape.has_text_frame:
            continue
        for paragraph in shape.text_frame.paragraphs:
            for run in paragraph.runs:
                run.font.color.rgb = color

# Apply the color scheme to all slides
for slide in prs.slides:
    set_slide_background(slide, secondary_color)
    set_text_color(slide, primary_color)

# Customize the table style on the case study slide
for shape in slide_case_study.shapes:
    if shape.has_table:
        table = shape.table
        for cell in table.iter_cells():
            cell.fill.solid()
            cell.fill.fore_color.rgb = accent_color
            for paragraph in cell.text_frame.paragraphs:
                for run in paragraph.runs:
                    run.font.color.rgb = primary_color
                    run.font.size = Pt(12)

# Save the updated presentation with the color scheme
updated_presentation_with_colors_path = "/mnt/data/Solar_for_Business_101_Color_Scheme.pptx"
prs.save(updated_presentation_with_colors_path)

updated_presentation_with_colors_path

